window.onload = () => {
  const BASE_URL =
    "https://wfstudiobe.morpheuslabs.io/api/v1/webhooks/cardtopia";
  const X_API_KEY = "ToVi388ImKgaGtK00srdyndmAQaKQZbzZKeoHk+JX+Y=";
  const REFERRER = "morpheuslabs.io";
  // Login

  const token = localStorage.getItem("token");

  if (token) {
    window.location.href = "/profile.html";
  }

  const loginEmail = $("#login-email");
  const loginPassword = $("#login-password");
  const loginError = $("#login-error-message");
  const btnLogin = $("#test-btn-2");

  btnLogin.on("click", async () => {
    if (loginEmail.val() === "" || loginPassword.val() === "") {
      loginError.text("Please enter email and password");
      return;
    }
    try {
      btnLogin.css("opacity", "0.5");
      btnLogin.prop("disabled", true);
      loginEmail.prop("disabled", true);
      loginPassword.prop("disabled", true);
      loginError.text("");
      $("#login-loading-animation").removeClass("hidden");
      $("#login-loading-text").addClass("hidden");
      await fetch(`${BASE_URL}/sign-in`, {
        headers: {
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify({
          username: loginEmail.val(),
          password: loginPassword.val(),
        }),
      })
        .then((res) => res.json())
        .then((res) => {
          if (res[0].data.status === false) {
            loginError.text(res[0].data.message);
            btnLogin.css("opacity", "1");
            btnLogin.prop("disabled", false);
            loginEmail.prop("disabled", false);
            loginPassword.prop("disabled", false);
            $("#login-loading-animation").addClass("hidden");
            $("#login-loading-text").removeClass("hidden");
          } else {
            localStorage.setItem("token", res[0].data);
            window.location.href = "/profile.html";
          }
        });
    } catch (error) {
      btnLogin.css("opacity", "1");
      btnLogin.prop("disabled", false);
      loginEmail.prop("disabled", false);
      loginPassword.prop("disabled", false);
      $("#login-loading-animation").addClass("hidden");
      $("#login-loading-text").removeClass("hidden");
      console.log(error);
      loginError.text(error.message);
      return null;
    }
  });
};

window.onload = () => {
  const BASE_URL =
    "https://wfstudiobe.morpheuslabs.io/api/v1/webhooks/cardtopia";
  const X_API_KEY = "ToVi388ImKgaGtK00srdyndmAQaKQZbzZKeoHk+JX+Y=";
  const REFERRER = "morpheuslabs.io";
  const token = localStorage.getItem("token");

  if (!token) {
    window.location.href = "/index.html";
  }

  const changePasswordBack = $("#change-password-back");
  const changePasswordBackSuccess = $("#change-password-back-success");

  changePasswordBack.on("click", () => {
    window.location.href = "/profile.html";
  });

  changePasswordBackSuccess.on("click", () => {
    window.location.href = "/profile.html";
  });

  const changePasswordOldPassword = $("#change-password-old-password");
  const changePasswordNewPassword = $("#change-password-new-password");
  const changePasswordConfirmNewPassword = $(
    "#change-password-confirm-new-password"
  );

  const changePasswordError = $("#change-password-error");
  const changePasswordSuccess = $("#change-password-success");
  const changePasswordForm = $("#change-password-form");
  const changePasswordSubmit = $("#change-password-confirm-submit");

  changePasswordSubmit.on("click", async () => {
    $("#change-password-loading-animation").removeClass("hidden");
    $("#change-password-loading-text").addClass("hidden");
    if (
      changePasswordConfirmNewPassword.val() !==
      changePasswordNewPassword.val()
    ) {
      changePasswordError.text(
        "New password and confirm new password do not match"
      );
      changePasswordSubmit.removeClass("login-loading");
      changePasswordSubmit.prop("disabled", false);
      $("#change-password-loading-animation").addClass("hidden");
      $("#change-password-loading-text").removeClass("hidden");
      return null;
    }

    if (
      changePasswordOldPassword.val() === "" ||
      changePasswordNewPassword.val() === "" ||
      changePasswordConfirmNewPassword.val() === ""
    ) {
      changePasswordError.text("Please enter all fields");
      changePasswordSubmit.removeClass("login-loading");
      changePasswordSubmit.prop("disabled", false);
      $("#change-password-loading-animation").addClass("hidden");
      $("#change-password-loading-text").removeClass("hidden");
      return null;
    }

    try {
      changePasswordSubmit.css("opacity", "0.5");
      changePasswordSubmit.prop("disabled", true);
      changePasswordOldPassword.prop("disabled", true);
      changePasswordNewPassword.prop("disabled", true);
      changePasswordConfirmNewPassword.prop("disabled", true);
      changePasswordError.text("");
      await fetch(`${BASE_URL}/admin/change-password`, {
        headers: {
          "X-API-KEY": X_API_KEY,
          Authorization: `Bearer ${token}`,
          Referer: REFERRER,
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify({
          old_password: changePasswordOldPassword.val(),
          new_password: changePasswordNewPassword.val(),
        }),
      })
        .then((res) => res.json())
        .then((res) => {
          if (res[0].data.status === false) {
            changePasswordError.text(res[0].data.message);
            changePasswordSubmit.css("opacity", "1");
            changePasswordSubmit.prop("disabled", false);
            changePasswordOldPassword.prop("disabled", false);
            changePasswordNewPassword.prop("disabled", false);
            changePasswordConfirmNewPassword.prop("disabled", false);
            $("#change-password-loading-animation").addClass("hidden");
            $("#change-password-loading-text").removeClass("hidden");
          } else {
            changePasswordSuccess.removeClass("hidden");
            changePasswordForm.addClass("hidden");
          }
        });
    } catch (error) {
      changePasswordSubmit.removeClass("login-loading");
      changePasswordSubmit.prop("disabled", false);
      changePasswordOldPassword.prop("disabled", false);
      changePasswordNewPassword.prop("disabled", false);
      changePasswordConfirmNewPassword.prop("disabled", false);
      console.log(error);
      changePasswordError.text(error.message);
      return null;
    }
  });
};

window.onload = () => {
  const X_API_KEY = "ToVi388ImKgaGtK00srdyndmAQaKQZbzZKeoHk+JX+Y=";
  const REFERRER = "morpheuslabs.io";
  const BASE_URL =
    "https://wfstudiobe.morpheuslabs.io/api/v1/webhooks/cardtopia";

  /**
   * @template T
   * @param {T} initialValue
   * @param {string} name: If name different undefined will auto save to localStorage
   * @returns {{value: T, setValue: (value:T)=> void, callbacks:((value:T,oldValue:T)=> void)[]}}
   */
  const useState = (initialValue, name) => {
    const state = {
      value: initialValue,
      setValue: (value) => {
        const oldValue = state.value;
        state.value = value;
        if (name) {
          if (typeof value === "string") {
            if (value) localStorage.setItem(name, value);
            else localStorage.removeItem(name);
          } else
            console.error(
              "Auto save localstorage only supported string value"
            );
        }
        if (value !== oldValue)
          state.callbacks.map((cb) => cb(value, oldValue));
      },
      callbacks: [],
    };
    return state;
  };

  const token = localStorage.getItem("token");
  if (!token) {
    window.location.href = "/";
  }

  const profileTabChangePassword = $("#transaction-change-password");
  const profileTabLogout = $("#transaction-logout");
  const profileTabProfile = $("#transaction-transaction");

  profileTabChangePassword.on("click", () => {
    window.location.href = "/change-password.html";
  });

  profileTabLogout.on("click", () => {
    localStorage.removeItem("token");
    window.location.href = "/index.html";
  });

  profileTabProfile.on("click", () => {
    window.location.href = "/profile.html";
  });

  const profileList = useState(null, []);
  const loadingProfile = useState(false, "loading-transaction");
  const total = useState(1, "total");
  const page = useState(1, "page");
  const getProfile = async (
    page_number = 1,
    text = "",
    setLoading = true
  ) => {
    if (setLoading) {
      loadingProfile.setValue(true);
      total.setValue(0);
    }

    if (text) {
      page.setValue(1);
    }

    let url = `${BASE_URL}/admin/transactions?page_number=${page_number}`;
    if (text) {
      url += `&search=${text}`;
    }

    const response = await fetch(url, {
      headers: {
        "X-API-KEY": X_API_KEY,
        Authorization: `Bearer ${token}`,
        Referer: REFERRER,
        "Content-Type": "application/json",
      },
    });

    const data = await response
      .json()
      .then((res) => {
        profileList.setValue(
          JSON.stringify(Object.values(res[0].data.transactions))
        );
        total.setValue(res[0].data.meta.count || 0);
      })
      .finally(() => {
        setLoading && loadingProfile.setValue(false);
      });
  };

  loadingProfile.callbacks.push((value, oldValue) => {
    if (value) {
      $("#loading-transaction").removeClass("hidden");
      $("#transaction-table").addClass("hidden");
    } else {
      $("#loading-transaction").addClass("hidden");
      $("#transaction-table").removeClass("hidden");
    }
  });

  profileList.callbacks.push((value, oldValue) => {
    const profileTemplate = $("#transaction-table-row");

    // Remove old NFT item
    if (JSON.parse(oldValue || "[]").length)
      $("#transaction-table-body").find(".remove-row").remove();

    if ((JSON.parse(value) || []).length === 0) {
      $("#transaction-table-body").find(".remove-row").remove();
      $("#pagination").find(".btn-pagination-render").remove();
      $("#transaction-table-body").append(
        `<tr class="remove-row-no-data">
          <td colspan="6" class="text-center">No data</td>
        </tr>`
      );
    }

    // Render NFT item
    (JSON.parse(value) || []).forEach((item, index) => {
      const profileRow = profileTemplate.clone();
      $("#transaction-table-body").find(".remove-row-no-data").remove();
      // Show all columns by removing hidden class
      profileRow.addClass("remove-row");
      profileRow.find("td").removeClass("hidden");

      // Set data for each column
      profileRow
        .find(".transaction-table-col-name")
        .text(item.tx_hash || "-");
      profileRow
        .find(".transaction-table-col-username")
        .text(item.username || "-");
      profileRow
        .find(".transaction-table-col-walletaress")
        .text(item.wallet_address || "-");
      profileRow
        .find(".transaction-table-col-email")
        .text(item.price || "-");
      profileRow
        .find(".transaction-table-col-phone")
        .text(
          item.created_at
            ? new Date(+item.created_at).toLocaleDateString()
            : "-"
        );

      profileRow
        .find(".transaction-table-col-joined_date")
        .text(
          item.status
            ? item.status === "mint_successed"
              ? "Mint Successed"
              : item.status === "minting"
              ? "Minting"
              : item.status === "created"
              ? "Created"
              : ""
            : "-"
        );

      // Add row to table body
      $("#transaction-table-body").append(profileRow);
    });
  });

  total.callbacks.push((value, oldValue) => {
    const pagination = $("#pagination");
    const totalPage = Math.ceil(value / 10);
    const btnTemplate = pagination.find(".pagination-btn-template");
    // pagination.empty();
    $(".btn-pagination-render").remove();
    for (let i = 1; i <= totalPage; i++) {
      if (i === 1) {
        const btn = btnTemplate
          .clone()
          .removeClass("hidden pagination-btn-template")
          .addClass("btn-pagination-render")
          .addClass(i === page.value ? "bg-[#252525] text-white" : "");

        btn.text(i);
        pagination.append(btn);

        if (page.value > 3) {
          const btnDots = btnTemplate
            .clone()
            .removeClass("hidden pagination-btn-template")
            .addClass("btn-pagination-render");
          btnDots.text("...");
          pagination.append(btnDots);
        }

        btn.on("click", () => {
          page.setValue(i);
          total.setValue(total.value);
          $("#pagination")
            .find(".btn-pagination")
            .removeClass("bg-[#252525] text-white");
          btn.addClass("btn-active");
          getProfile(i);
        });
      } else if (i === totalPage) {
        if (page.value < totalPage - 2) {
          const btnDots = btnTemplate
            .clone()
            .removeClass("hidden pagination-btn-template")
            .addClass("btn-pagination-render");
          btnDots.text("...");
          pagination.append(btnDots);
        }

        const btn = btnTemplate
          .clone()
          .removeClass("hidden pagination-btn-template")
          .addClass("btn-pagination-render")
          .addClass(i === page.value ? "bg-[#252525] text-white" : "");

        btn.text(i);
        pagination.append(btn);
        btn.on("click", () => {
          page.setValue(i);
          total.setValue(total.value);
          $("#pagination")
            .find(".btn-pagination")
            .removeClass("bg-[#252525] text-white");
          btn.addClass("bg-[#252525] text-white");
          getProfile(i);
        });
      } else {
        if (
          i === page.value - 1 ||
          i === page.value + 1 ||
          i === page.value - 2 ||
          i === page.value + 2 ||
          i === page.value
        ) {
          const btn = btnTemplate
            .clone()
            .removeClass("hidden pagination-btn-template")
            .addClass("btn-pagination-render")
            .addClass(i === page.value ? "bg-[#252525] text-white" : "");

          btn.text(i);
          pagination.append(btn);
          btn.on("click", () => {
            page.setValue(i);
            total.setValue(total.value);
            $("#pagination")
              .find(".btn-pagination")
              .removeClass("bg-[#252525] text-white");
            btn.addClass("bg-[#252525] text-white");
            getProfile(i);
          });
        }
      }
    }
  });

  $("#transaction-search-input").on("keyup", (e) => {
    if (e.key === "Enter") {
      getProfile(1, e.target.value);
    }
  });

  getProfile(1);

  setInterval(() => {
    const textSearch = $("#transaction-search-input").val();
    if (textSearch) {
      getProfile(1, textSearch, false);
    } else {
      getProfile(page.value, "", false);
    }
  }, 10000);
};

