      loading.setValue(false);
      return;
    }

    const nfts = await saleContractInstance.methods
      .getSaleList(0, total.value) // TODO: change to page and limit
      .call();
    nftList.setValue(
      nfts.map((item) => ({
        price: item.price,
        tokenId: item.tokenId,
      }))
    );
    console.log("🚀 ~ getNFTs ~ nftList:", nftList);
    loading.setValue(false);
  };

